        // Mouse over province => kanji is marked
        function kanjiSquareOn(clicked_id) {
            document.getElementById(clicked_id+"Square").style.display = "block";
        }
        function kanjiSquareOff(clicked_id) {
            document.getElementById(clicked_id+"Square").style.display = "none";
        }    

        // Show kanji => rectangles and kanji numbers are not shown
        function showAll() {
            var el = document.getElementsByClassName("ans")
            for(var i = 0; i < el.length; i++){
                el[i].style.display = "none";
            }
	    document.getElementById("hideAll").style.display = "block";
	    document.getElementById("showAll").style.display = "none";
        }
        // Hide kanji => rectangles and kanji numbers are shown on top
        function hideAll() {
            var el = document.getElementsByClassName("ans")
            for(var i = 0; i < el.length; i++){
                el[i].style.display = "block";
            }
	    document.getElementById("hideAll").style.display = "none";
	    document.getElementById("showAll").style.display = "block";
        }   

        function toggleKanji(clicked_id) {
            id = clicked_id.slice(-2)
            var style = document.getElementById("kanjiCoverRect"+id).style.display;
            if (style === "none"){
                document.getElementById("kanjiCoverRect"+id).style.display = "block";
                document.getElementById("provinceLabel"+id).style.display = "block";
                document.getElementById("kanjiLabel"+id).style.display = "block";
            }
            else {
                document.getElementById("kanjiCoverRect"+id).style.display = "none";
                document.getElementById("provinceLabel"+id).style.display = "none";
                document.getElementById("kanjiLabel"+id).style.display = "none";
            }
        }
